import streamlit as st
from random import randint
import time

st.title('Sphinx Of Black Quartz Judge My Vow')
st.image('data:image/jpeg;base64,')
team_members = st.multiselect(
    'Who is at retro?',
    ['Dane', 'Chris', 'Troy', 'Ian', 'Blackjack', 'Mandy', 'Nick'])
# st.write(len(team_members))
# st.write(randint(0, len(team_members)-1))

if team_members and st.button('JUDGE MY VOW'):
    with st.spinner('Selecting the chosen one...'):
        time.sleep(3)
        the_chosen_one = team_members[randint(0, len(team_members)-1)]
        st.balloons()
        st.header(f'The chosen one is {the_chosen_one}')